const services = [
  { name: 'Sonarr', url: '${process.env.SONARR_URL}/api/v3/system/status', headers: { 'X-Api-Key': process.env.SONARR_API_KEY } },
  { name: 'Radarr', url: '${process.env.RADARR_URL}/api/v3/system/status', headers: { 'X-Api-Key': process.env.RADARR_API_KEY } },
  { name: 'Prowlarr', url: '${process.env.PROWLARR_URL}/api/v1/system/status', headers: { 'X-Api-Key': process.env.PROWLARR_API_KEY } },
  { name: 'SABnzbd', url: '${process.env.SABNZBD_URL}/api?mode=version&apikey=${process.env.SABNZBD_API_KEY}', headers: {} },
  { name: 'Overseerr', url: '${process.env.OVERSEERR_URL}/api/v1/status', headers: { 'X-Api-Key': process.env.OVERSEERR_API_KEY } }
];

const results = [];
const failures = [];

for (const svc of services) {
  try {
    await this.helpers.httpRequest({
      method: 'GET',
      url: svc.url,
      headers: svc.headers,
      timeout: 10000
    });
    results.push(`✅ ${svc.name}: OK`);
  } catch (err) {
    failures.push(svc.name);
    results.push(`❌ ${svc.name}: DOWN - ${err.message}`);
  }
}

return [{
  json: {
    hasFailures: failures.length > 0,
    failureCount: failures.length,
    failedServices: failures.join(', '),
    summary: results.join('\n'),
    timestamp: new Date().toISOString()
  }
}];
