// Gmail Labels & Contacts - Discovery/Reporting Tool
// Lists all labels with message counts and keeper contact group members
// Returns both structured JSON (for Homepage widget) and text report (for Discord)

const items = $input.all();
const globals = $('Globals').first().json;
const googleClientId = globals.GOOGLE_CLIENT_ID;
const googleClientSecret = globals.GOOGLE_CLIENT_SECRET;
const googleRefreshToken = globals.GOOGLE_REFRESH_TOKEN;

// Get OAuth2 access token
const tokenBody = `client_id=${encodeURIComponent(googleClientId)}&client_secret=${encodeURIComponent(googleClientSecret)}&refresh_token=${encodeURIComponent(googleRefreshToken)}&grant_type=refresh_token`;
const tokenResp = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://oauth2.googleapis.com/token',
  body: tokenBody,
  headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
});
const accessToken = tokenResp.access_token;

async function apiRequest(url, method = 'GET') {
  return await this.helpers.httpRequest({
    method,
    url,
    headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' }
  });
}

function parseKeepDaysLabel(labelName) {
  const match = labelName.match(/^KEEP_(\d{1,3})(D|W|M)$/i);
  if (!match) return null;
  const value = parseInt(match[1], 10);
  const unit = match[2].toLowerCase();
  const multipliers = { d: 1, w: 7, m: 30 };
  const days = value * (multipliers[unit] || 1);
  return (days >= 1 && days <= 3650) ? days : null;
}

const errors = [];

// PHASE 1: Fetch all labels with message counts
const labelsResp = await apiRequest.call(this, 'https://gmail.googleapis.com/gmail/v1/users/me/labels');
const allLabels = labelsResp.labels || [];

const userLabels = [];
const systemLabels = [];
const keepLabels = [];
let inboxUnread = 0;
let inboxTotal = 0;

for (const label of allLabels) {
  try {
    const detail = await apiRequest.call(this, `https://gmail.googleapis.com/gmail/v1/users/me/labels/${label.id}`);
    const info = {
      name: detail.name,
      id: detail.id,
      type: detail.type,
      messagesTotal: detail.messagesTotal || 0,
      messagesUnread: detail.messagesUnread || 0,
      threadsTotal: detail.threadsTotal || 0,
      threadsUnread: detail.threadsUnread || 0,
    };

    if (detail.name === 'INBOX') {
      inboxUnread = info.messagesUnread;
      inboxTotal = info.messagesTotal;
    }

    if (detail.type === 'system') {
      systemLabels.push(info);
    } else {
      userLabels.push(info);
    }

    if (detail.name.toUpperCase() === 'KEEP' || parseKeepDaysLabel(detail.name) !== null) {
      const retDays = parseKeepDaysLabel(detail.name);
      keepLabels.push({
        name: detail.name,
        retention: retDays ? `${retDays} days` : 'forever',
        messagesTotal: info.messagesTotal,
        threadsTotal: info.threadsTotal,
      });
    }
  } catch (e) {
    errors.push(`Label ${label.name}: ${e.message}`);
  }
}

userLabels.sort((a, b) => a.name.localeCompare(b.name));
systemLabels.sort((a, b) => a.name.localeCompare(b.name));

// PHASE 2: Fetch keeper contacts
let keeperContactInfo = [];
let keeperGroupName = null;

try {
  const groupsResp = await apiRequest.call(this, 'https://people.googleapis.com/v1/contactGroups?pageSize=1000');
  const contactGroups = (groupsResp.contactGroups || []).filter(g => g.groupType === 'USER_CONTACT_GROUP');
  const keeperGroup = contactGroups.find(g => g.name.toLowerCase() === 'keeper');

  if (keeperGroup) {
    keeperGroupName = keeperGroup.name;
    const groupDetail = await apiRequest.call(this, `https://people.googleapis.com/v1/${keeperGroup.resourceName}?maxMembers=1000`);
    const memberResourceNames = groupDetail.memberResourceNames || [];

    if (memberResourceNames.length > 0) {
      for (let i = 0; i < memberResourceNames.length; i += 50) {
        const batch = memberResourceNames.slice(i, i + 50);
        const params = batch.map(r => `resourceNames=${encodeURIComponent(r)}`).join('&');
        const peopleResp = await apiRequest.call(this, `https://people.googleapis.com/v1/people:batchGet?${params}&personFields=emailAddresses,names`);

        for (const resp of (peopleResp.responses || [])) {
          const person = resp.person || {};
          const names = (person.names || []).map(n => n.displayName).filter(Boolean);
          const emails = (person.emailAddresses || []).map(e => e.value).filter(Boolean);
          if (emails.length > 0) {
            keeperContactInfo.push({
              name: names[0] || '(no name)',
              emails: emails.map(e => e.toLowerCase()),
            });
          }
        }
      }
    }
  }
} catch (e) {
  errors.push(`Contacts API: ${e.message}`);
}

keeperContactInfo.sort((a, b) => a.name.localeCompare(b.name));

// PHASE 3: Build text report (for Discord)
const totalLabels = allLabels.length;
let report = `Gmail Labels & Contacts Report\n\n`;

if (keepLabels.length > 0) {
  report += `KEEP Labels (${keepLabels.length}):\n`;
  for (const kl of keepLabels) {
    report += `  ${kl.name} - ${kl.retention}, ${kl.threadsTotal.toLocaleString()} threads, ${kl.messagesTotal.toLocaleString()} msgs\n`;
  }
  report += `\n`;
}

report += `User Labels (${userLabels.length}):\n`;
for (const l of userLabels) {
  const unread = l.messagesUnread > 0 ? `, ${l.messagesUnread.toLocaleString()} unread` : '';
  report += `  ${l.name} (${l.messagesTotal.toLocaleString()} msgs${unread})\n`;
}
report += `\n`;

report += `System Labels (${systemLabels.length}):\n`;
const activeSysLabels = systemLabels.filter(l => l.messagesTotal > 0);
for (const l of activeSysLabels) {
  const unread = l.messagesUnread > 0 ? `, ${l.messagesUnread.toLocaleString()} unread` : '';
  report += `  ${l.name} (${l.messagesTotal.toLocaleString()} msgs${unread})\n`;
}
if (systemLabels.length > activeSysLabels.length) {
  report += `  ... and ${systemLabels.length - activeSysLabels.length} empty system labels\n`;
}
report += `\n`;

if (keeperGroupName) {
  const totalEmails = keeperContactInfo.reduce((sum, c) => sum + c.emails.length, 0);
  report += `Keeper Contacts ("${keeperGroupName}" group, ${keeperContactInfo.length} members, ${totalEmails} emails):\n`;
  for (const c of keeperContactInfo) {
    report += `  ${c.name}: ${c.emails.join(', ')}\n`;
  }
} else {
  report += `Keeper Contacts: No "keeper" contact group found\n`;
}

if (errors.length > 0) {
  report += `\nErrors:\n`;
  for (const e of errors) {
    report += `  - ${e}\n`;
  }
}

report += `\nTotal: ${totalLabels} labels (${userLabels.length} user, ${systemLabels.length} system)`;

// PHASE 4: Build structured data (for Homepage widget)
const keepThreadsTotal = keepLabels.reduce((sum, kl) => sum + kl.threadsTotal, 0);

return [{ json: {
  report,
  inboxUnread,
  inboxTotal,
  totalLabels,
  userLabels: userLabels.length,
  keepLabels: keepLabels.length,
  keepThreads: keepThreadsTotal,
  keeperContacts: keeperContactInfo.length,
  errors: errors.length
} }];
