// Check Plex recently added items (last 2 hours)
const env = $input.first().json;
const twoHoursAgo = Math.floor(Date.now() / 1000) - 7200;

const sections = [];
let totalNew = 0;

// Get all library sections first
let libraries;
try {
  const libResp = await this.helpers.httpRequest({
    method: 'GET',
    url: `${env.PLEX_URL}/library/sections`,
    headers: { 'X-Plex-Token': env.PLEX_TOKEN, 'Accept': 'application/json' },
    timeout: 15000
  });
  libraries = libResp?.MediaContainer?.Directory || [];
} catch (err) {
  return [{ json: { hasNew: false, error: err.message } }];
}

for (const lib of libraries) {
  try {
    const recentResp = await this.helpers.httpRequest({
      method: 'GET',
      url: `${env.PLEX_URL}/library/sections/${lib.key}/recentlyAdded?X-Plex-Container-Start=0&X-Plex-Container-Size=20`,
      headers: { 'X-Plex-Token': env.PLEX_TOKEN, 'Accept': 'application/json' },
      timeout: 15000
    });
    const items = recentResp?.MediaContainer?.Metadata || [];
    const newItems = items.filter(i => i.addedAt > twoHoursAgo);

    if (newItems.length > 0) {
      totalNew += newItems.length;
      const emoji = lib.type === 'movie' ? 'üé¨' : lib.type === 'show' ? 'üì∫' : 'üéµ';
      const itemList = newItems.map(i => {
        if (lib.type === 'show') {
          return `  ‚Ä¢ ${i.grandparentTitle || i.parentTitle || i.title} - ${i.title}`;
        }
        return `  ‚Ä¢ ${i.title}${i.year ? ` (${i.year})` : ''}`;
      });
      sections.push(`${emoji} ${lib.title} (${newItems.length} new):\n${itemList.join('\n')}`);
    }
  } catch (err) {
    // Skip failed library
  }
}

if (totalNew === 0) {
  return [{ json: { hasNew: false } }];
}

return [{
  json: {
    hasNew: true,
    title: `üéûÔ∏è ${totalNew} New Item(s) Added to Plex`,
    message: sections.join('\n\n'),
    priority: 3
  }
}];
