const globals = $('Globals').first().json;
const PVE = globals.PVE_API_URL;
const TOKEN = globals.PVE_API_TOKEN;
const headers = { Authorization: `PVEAPIToken=${TOKEN}` };
const opts = { method: 'GET', headers, skipSslCertificateValidation: true, timeout: 15000 };

function formatUptime(seconds) {
  const d = Math.floor(seconds / 86400);
  const h = Math.floor((seconds % 86400) / 3600);
  if (d > 0) return `${d}d ${h}h`;
  return `${h}h`;
}

function formatBytes(bytes) {
  const gb = bytes / (1024 * 1024 * 1024);
  if (gb >= 1024) return `${(gb / 1024).toFixed(1)} TB`;
  return `${gb.toFixed(1)} GB`;
}

const parts = [];
let totalVmsRunning = 0, totalVmsTotal = 0;
let totalCtsRunning = 0, totalCtsTotal = 0;
let nodesOnline = 0;

try {
  const nodesResp = await this.helpers.httpRequest({ ...opts, url: `${PVE}/api2/json/nodes` });
  const nodes = (nodesResp.data || []).sort((a, b) => a.node.localeCompare(b.node));

  for (const node of nodes) {
    const name = node.node;

    if (node.status !== 'online') {
      parts.push(`\u{1f534} **${name}** \u2014 OFFLINE`);
      continue;
    }
    nodesOnline++;

    const cpuPct = Math.round(node.cpu * 100);
    const memPct = Math.round((node.mem / node.maxmem) * 100);
    const diskPct = Math.round((node.disk / node.maxdisk) * 100);
    const uptime = formatUptime(node.uptime);

    let vmRunning = 0, vmTotal = 0, ctRunning = 0, ctTotal = 0;

    for (const guestType of ['qemu', 'lxc']) {
      try {
        const resp = await this.helpers.httpRequest({ ...opts, url: `${PVE}/api2/json/nodes/${name}/${guestType}` });
        const guests = resp.data || [];
        for (const g of guests) {
          if (g.template) continue;
          if (guestType === 'qemu') {
            vmTotal++;
            totalVmsTotal++;
            if (g.status === 'running') { vmRunning++; totalVmsRunning++; }
          } else {
            ctTotal++;
            totalCtsTotal++;
            if (g.status === 'running') { ctRunning++; totalCtsRunning++; }
          }
        }
      } catch (e) { /* skip */ }
    }

    const memUsed = formatBytes(node.mem);
    const memMax = formatBytes(node.maxmem);
    const guestStr = [];
    if (vmTotal > 0) guestStr.push(`VMs: ${vmRunning}/${vmTotal}`);
    if (ctTotal > 0) guestStr.push(`CTs: ${ctRunning}/${ctTotal}`);

    parts.push(`**\u{1f5a5}\ufe0f ${name}**\nCPU: ${cpuPct}% | Mem: ${memPct}% (${memUsed}/${memMax}) | Disk: ${diskPct}% | Up: ${uptime} | ${guestStr.join(' | ')}`);
  }
} catch (err) {
  parts.push(`**\u274c Error**\nFailed to query Proxmox API: ${err.message}`);
}

const today = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric', timeZone: 'America/New_York' });

return [{
  json: {
    title: `\u{1f4ca} Daily Proxmox Report \u2014 ${today}`,
    color: 3447003,
    description: parts.join('\n\n'),
    footer: `${nodesOnline} nodes online | ${totalVmsRunning} VMs running | ${totalCtsRunning} CTs running`,
    timestamp: new Date().toISOString()
  }
}];
