const creds = $('Set Credentials').first().json;
const trashDay = $input.first().json;

const EVENT_TITLE = creds.trashEventTitle || 'Trash prep day';

const tokenBody = `client_id=${encodeURIComponent(creds.googleClientId)}&client_secret=${encodeURIComponent(creds.googleClientSecret)}&refresh_token=${encodeURIComponent(creds.googleRefreshToken)}&grant_type=refresh_token`;
const tokenResp = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://oauth2.googleapis.com/token',
  body: tokenBody,
  headers: { 'Content-Type': 'application/x-www-form-urlencoded' }
});
const accessToken = tokenResp.access_token;

const CALENDAR_ID = '52pai5sgrbqhh12h7mv1o3rb08@group.calendar.google.com';

function formatDate(d) {
  return `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,'0')}-${String(d.getDate()).padStart(2,'0')}`;
}

function addDays(dateStr, n) {
  const d = new Date(dateStr + 'T00:00:00');
  d.setDate(d.getDate() + n);
  return formatDate(d);
}

const takeOutDateStr = trashDay.takeOutDate;
const nextDateStr = addDays(takeOutDateStr, 1);

const events = [];
events.push({
  summary: EVENT_TITLE,
  start: { date: takeOutDateStr },
  end: { date: nextDateStr },
  reminders: { useDefault: false, overrides: [{ method: 'popup', minutes: 540 }] }
});

if (trashDay.delayed) {
  // Create a delay notice event on the default (non-delayed) take-out day
  const defaultTakeOutDate = addDays(trashDay.pickupDate, -2);
  const defaultPickupDate = addDays(trashDay.pickupDate, -1);
  events.push({
    summary: `Trash delay due to ${trashDay.holiday}`,
    start: { date: defaultTakeOutDate },
    end: { date: defaultPickupDate },
    reminders: { useDefault: false }
  });
}

const results = [];
for (const event of events) {
  try {
    await this.helpers.httpRequest({
      method: 'POST',
      url: `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(CALENDAR_ID)}/events`,
      headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' },
      body: JSON.stringify(event)
    });
    results.push(`Created: ${event.summary} on ${event.start.date}`);
  } catch (err) {
    results.push(`Error creating '${event.summary}': ${err.message}`);
  }
}

let summary = `Trash Pickup Schedule (week of ${trashDay.weekOf})\n`;
summary += `Default pickup day: Friday\n`;
if (trashDay.delayed) {
  summary += `Holiday: ${trashDay.holiday}\n`;
  summary += `Delayed pickup: ${trashDay.pickupDay} ${trashDay.pickupDate}\n`;
}
summary += `Take out trash: ${trashDay.takeOutDay} ${trashDay.takeOutDate}\n\n`;
summary += results.join('\n');

return [{ json: { title: 'Trash Pickup', message: summary, priority: 3 } }];
