const globals = $('Globals').first().json;
const PVE = globals.PVE_API_URL;
const PVE_TOKEN = globals.PVE_API_TOKEN;
const TECH_URL = globals.TECHNITIUM_URL;
const TECH_TOKEN = globals.TECHNITIUM_TOKEN;
const http = this.helpers.httpRequest.bind(this.helpers);
const pveHeaders = { Authorization: `PVEAPIToken=${PVE_TOKEN}` };
const pveOpts = { method: 'GET', headers: pveHeaders, skipSslCertificateValidation: true, timeout: 15000 };

// PVE version
let pveVersion = 'unknown';
try {
  const verResp = await http({ ...pveOpts, url: `${PVE}/api2/json/version` });
  pveVersion = verResp.data?.version || 'unknown';
} catch (e) {
  pveVersion = 'error';
}

// PVE nodes — skip apt (requires Sys.Modify, token only has PVEAuditor)
const pveNodes = [];
try {
  const nodesResp = await http({ ...pveOpts, url: `${PVE}/api2/json/nodes` });
  for (const node of (nodesResp.data || [])) {
    pveNodes.push({ name: node.node });
  }
} catch (e) {}

// Technitium version via /api/settings/get
let techVersion = 'unknown';
try {
  const techResp = await http({
    method: 'GET',
    url: `${TECH_URL}/api/settings/get?token=${TECH_TOKEN}`,
    timeout: 10000
  });
  techVersion = techResp.response?.version || 'unknown';
} catch (e) {
  techVersion = 'error';
}

// Latest GitHub releases
function normalizeTag(name, tag) {
  if (['sonarr','radarr','prowlarr','overseerr','sabnzbd','tautulli','plex'].includes(name)) {
    return tag.replace(/-ls\d+$/, '');
  }
  if (['traefik','zigbee2mqtt'].includes(name)) {
    return tag.replace(/^v/, '');
  }
  if (name === 'n8n') {
    return tag.replace(/^n8n@/, '').replace(/^v/, '');
  }
  return tag;
}

const githubServices = [
  { name: 'sonarr', repo: 'linuxserver/docker-sonarr' },
  { name: 'radarr', repo: 'linuxserver/docker-radarr' },
  { name: 'prowlarr', repo: 'linuxserver/docker-prowlarr' },
  { name: 'overseerr', repo: 'linuxserver/docker-overseerr' },
  { name: 'sabnzbd', repo: 'linuxserver/docker-sabnzbd' },
  { name: 'tautulli', repo: 'linuxserver/docker-tautulli' },
  { name: 'plex', repo: 'linuxserver/docker-plex' },
  { name: 'traefik', repo: 'traefik/traefik' },
  { name: 'zigbee2mqtt', repo: 'Koenkk/zigbee2mqtt' },
  { name: 'n8n', repo: 'n8n-io/n8n' }
];

const latestVersions = {};
for (const svc of githubServices) {
  try {
    const resp = await http({
      method: 'GET',
      url: `https://api.github.com/repos/${svc.repo}/releases/latest`,
      headers: { 'User-Agent': 'n8n-version-audit', Accept: 'application/vnd.github.v3+json' },
      timeout: 10000
    });
    latestVersions[svc.name] = normalizeTag(svc.name, resp.tag_name || '');
  } catch (e) {
    latestVersions[svc.name] = 'unknown';
  }
}

return [{
  json: {
    type: 'api_results',
    pveVersion,
    pveNodes,
    techVersion,
    latestVersions
  }
}];

const items = $input.all();
const aptResults = [];
const dockerResults = [];
const healthIssues = [];
const restartIssues = [];
const dockhandStacks = [];
const dockhandExecFails = [];
let apiData = null;

for (const item of items) {
  if (item.json.type === 'api_results') { apiData = item.json; continue; }
  const stdout = (item.json.stdout || '').trim();
  if (!stdout) continue;
  for (const line of stdout.split('\n')) {
    const t = line.trim();
    if (!t) continue;
    if (t.startsWith('apt|')) {
      const p = t.split('|');
      aptResults.push({ host: p[1], pending: parseInt(p[2] || '0', 10), reboot: (p[3] || '').trim() === 'yes' });
    } else if (t.startsWith('docker|')) {
      const p = t.split('|');
      dockerResults.push({ container: p[1], version: p[2] || 'unknown' });
    } else if (t.startsWith('health|')) {
      const p = t.split('|');
      healthIssues.push({ container: p[1], state: p[2], status: p.slice(3).join('|') });
    } else if (t.startsWith('restarts|')) {
      const p = t.split('|');
      restartIssues.push({ container: p[1], count: parseInt(p[2] || '0', 10) });
    } else if (t.startsWith('stack|')) {
      const p = t.split('|');
      dockhandStacks.push({ name: p[1], syncStatus: p[2], error: p.slice(3).join('|') });
    } else if (t.startsWith('exec_fail|')) {
      const p = t.split('|');
      dockhandExecFails.push({ name: p[1], status: p[2], error: p.slice(3).join('|') });
    }
  }
}

function norm(name, ver) {
  let v = ver.replace(/-ls\d+$/, '').replace(/^v/, '');
  if (name === 'plex') v = v.replace(/-[a-f0-9]{9,}$/, '');
  return v;
}

// --- Embed 1: Software Versions ---
const latest = apiData?.latestVersions || {};
let anyUpdate = false;
let desc1 = '**--- Docker Containers ---**\n';
dockerResults.sort((a, b) => a.container.localeCompare(b.container));

for (const d of dockerResults) {
  const cur = norm(d.container, d.version);
  const lat = latest[d.container] ? norm(d.container, latest[d.container]) : null;
  if (!lat || lat === 'unknown' || cur === lat || cur.startsWith(lat) || lat.startsWith(cur)) {
    desc1 += '\u2705 ' + d.container + ': ' + cur + '\n';
  } else {
    desc1 += '\u26A0\uFE0F **' + d.container + '**: ' + cur + ' \u2192 ' + lat + '\n';
    anyUpdate = true;
  }
}

desc1 += '\n**--- Infrastructure ---**\n';
const pveVer = apiData?.pveVersion || 'unknown';
const pveNodes = apiData?.pveNodes || [];
desc1 += '\uD83D\uDDA5\uFE0F PVE: ' + pveVer + ' (' + pveNodes.length + ' nodes)\n';
desc1 += '\u2705 Technitium DNS: v' + (apiData?.techVersion || 'unknown') + '\n';

// --- Embed 2: System Updates ---
let desc2 = '';
aptResults.sort((a, b) => a.host.localeCompare(b.host));
let anyApt = false;
for (const h of aptResults) {
  if (h.pending > 0) {
    let line = '\uD83D\uDCE6 **' + h.host + '**: ' + h.pending + ' pending';
    if (h.reboot) line += ' \u26A0\uFE0F reboot';
    desc2 += line + '\n';
    anyApt = true;
  } else {
    desc2 += '\u2705 ' + h.host + ': Up to date\n';
  }
}

// --- Embed 3: Container & Stack Health ---
let desc3 = '';
let anyHealthIssue = false;

if (healthIssues.length > 0) {
  desc3 += '**--- Container Issues ---**\n';
  for (const h of healthIssues) {
    desc3 += '\u274C **' + h.container + '**: ' + h.state + ' (' + h.status + ')\n';
    anyHealthIssue = true;
  }
}

if (restartIssues.length > 0) {
  if (desc3) desc3 += '\n';
  desc3 += '**--- Restart Loops ---**\n';
  for (const r of restartIssues) {
    desc3 += '\u26A0\uFE0F **' + r.container + '**: ' + r.count + ' restarts\n';
    anyHealthIssue = true;
  }
}

const failedStacks = dockhandStacks.filter(s => !['success', 'deployed', 'synced'].includes(s.syncStatus));
if (desc3) desc3 += '\n';
desc3 += '**--- Dockhand Stacks ---**\n';
if (dockhandStacks.length === 0) {
  desc3 += '\u26A0\uFE0F Could not query Dockhand\n';
  anyHealthIssue = true;
} else if (failedStacks.length > 0) {
  for (const s of failedStacks) {
    desc3 += '\u274C **' + s.name + '**: ' + s.syncStatus + (s.error ? ' \u2014 ' + s.error : '') + '\n';
    anyHealthIssue = true;
  }
  const okCount = dockhandStacks.length - failedStacks.length;
  if (okCount > 0) desc3 += '\u2705 ' + okCount + ' stacks synced\n';
} else {
  desc3 += '\u2705 All ' + dockhandStacks.length + ' stacks synced\n';
}

if (dockhandExecFails.length > 0) {
  desc3 += '\n**--- Failed Deployments (7d) ---**\n';
  for (const e of dockhandExecFails) {
    desc3 += '\u274C **' + e.name + '**: ' + (e.error || 'unknown error') + '\n';
    anyHealthIssue = true;
  }
}

if (!healthIssues.length && !restartIssues.length && failedStacks.length === 0 && !dockhandExecFails.length && dockhandStacks.length > 0) {
  desc3 = '\u2705 All containers healthy\n\u2705 All ' + dockhandStacks.length + ' Dockhand stacks synced\n\u2705 No failed deployments (7d)';
}

const today = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric', timeZone: 'America/New_York' });
const cCount = dockerResults.length;
const hCount = aptResults.length;

return [{
  json: {
    embeds: [
      {
        title: '\uD83D\uDD0D Software Versions \u2014 ' + today,
        description: desc1,
        color: anyUpdate ? 16776960 : 3066993,
        timestamp: new Date().toISOString()
      },
      {
        title: '\uD83D\uDCE6 System Updates \u2014 ' + today,
        description: desc2,
        color: anyApt ? 16776960 : 3066993,
        timestamp: new Date().toISOString()
      },
      {
        title: '\uD83D\uDC33 Container & Stack Health \u2014 ' + today,
        description: desc3,
        color: anyHealthIssue ? 15158332 : 3066993,
        footer: { text: cCount + ' containers | ' + hCount + ' hosts | ' + pveNodes.length + ' PVE nodes | ' + dockhandStacks.length + ' stacks' },
        timestamp: new Date().toISOString()
      }
    ],
    latestVersionsJson: JSON.stringify(latest)
  }
}];
